sys_script_include.script:
var LocationFetcherFetcher = Class.create();
LocationFetcherFetcher.prototype = {
    initialize: function() {},

    fetchCityFromLocationFetcher: function(locationId) {
        var result = '';
        if (!locationId) {
            return result;
        }

        // Query cmn_location table based on the input location (Street)
        var locationGR = new GlideRecord('cmn_location');
        if (locationGR.get(locationId)) {
            var locationName = locationGR.getValue('name');

            // Query the cmn_location table for city using the location name
            var cityGR = new GlideRecord('cmn_location');
            cityGR.addQuery('name', locationName);
            cityGR.query();

            var cities = [];
            while (cityGR.next()) {
                cities.push(cityGR.getValue('city'));
            }

            // If there are multiple cities, update city field with the first one
            if (cities.length > 0) {
                result = cities[0];  // Pick the first one or apply additional logic to select the right city
            }
        }

        return result;
    },

    type: 'LocationFetcherFetcher'
};
